import React from 'react'

export default class ModuleListItem extends React.Component {
    constructor(props) {
        super(props)

        this.module = props.module
        this.deleteModule = props.deleteModule
        this.selectModule = props.selectModule
        this.selectedModule = props.selectedModule
    }


    render() {
        return (
            <li className={this.module === this.selectedModule? "list-group-item active":"list-group-item"} onClick={() => this.selectModule(module)}>
                {module.title}
                <div className="btn-group float-right">
                    <button className="btn btn-warning">Edit</button>
                    {/* <button className="btn btn-warning" onClick={() => editModule(module.id)}>Edit</button> */}
                    <button className="btn btn-danger" onClick={() => this.deleteModule(module.id)}>X</button>
                </div>
            </li>
        )
    }
}
