import React from 'react'

export default class CourseCard
    extends React.Component
{
    constructor(props) {
        super(props)
        this.state = {}
    }

    deleteCourse = (id) => {
        console.log('deleteCourse ' + id)
        this.setState({
            courses: this.state.courses.filter(course => course.id !== id)
        })
    }


    render() {
        return (
            <div className="card" styles={{width: '18rem'}}>
                <img className="card-img-top"
                     src="https://picsum.photos/300/200"/>
                <div className="card-body">
                    <h5 className="card-title">
                        {this.props.course.title}
                    </h5>
                    <p className="card-text">Card text.</p>
                    <a href={`/course-editor/${this.props.course.id}`} className="btn btn-primary">More...</a>
                    <button className="btn btn-danger" onClick={() => this.deleteCourse(course.id)}>Delete</button>
                </div>
            </div>
        )
    }
}
