import React from 'react'

export default class LessonTabs extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            lesson: {
                id: -1,
                title: 'New Lesson'
            },
            lessons: this.props.lessons
        }
    }

    createLesson = () => {
        // this.state.module.push()
        this.state.module.id = (new Date()).getTime()
        this.setState({
            modules: [...this.state.lessons, this.state.lesson]
        })
    }

    titleChanged = (event) => {
        console.log(event.target.value)
        this.setState({
            lesson: {
                title: event.target.value,
                id: (new Date()).getTime()
            }
        })
    }

    render() {
        return(
            <ul className="nav nav-tabs">
                <li className="nav-item">
                    <a className="nav-link active" href="#">Build</a>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="#">Pages</a>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="#">Theme</a>
                </li>
                <li className="nav-item">
                    <a className="nav-link disabled" href="#">Store</a>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="#">Apps</a>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="#">Settings</a>
                </li>
                <li className="nav-item">
                    <div className="input-group-append">
                        <input
                            onChange={this.titleChanged}
                            defaultValue={this.state.lesson.title}
                            className="form-control"/>
                        <button onClick={this.createLesson} className="btn btn-primary">
                            Add Lesson
                        </button>
                    </div>
                </li>
            </ul>
        )
    }
}
