import React from 'react'
import ModuleList from "./ModuleList";
import LessonTabs from "./LessonTabs";
import TopicPills from "./TopicPills";

export default class CourseEditor
    extends React.Component {
    constructor(props) {
        super(props)
        const pathname = window.location.pathname
        const paths = pathname.split('/')
        const courseId = paths[3]
        this.courses = props.courses 
        this.state = {
            courseId: courseId,
            course: this.props.course,
            selectedModule: this.props.course.modules[0]
        }

    }


    selectModule = module => {
        this.setState({
            selectedModule: module,
            selectedLesson: module.lessons,
            selectedTopic: module.lessons[0].topics[0]
        })
    }



    render() {
        return(
            <div>
                <h2>{this.state.course.title} {this.state.course.id}</h2>
                <div className="row">
                    <div className="col-3 left list-group">
                        <ModuleList selectModule={this.selectModule}
                                    modules={this.state.course.modules}/>
                    </div>
                    <div className="col-8 right">
                        <LessonTabs lessons={this.state.selectedModule.lessons}/>
                        <br/>
                        <TopicPills/>
                    </div>
                </div>
            </div>
        )
    }
}
