import React from 'react'
import ModuleListItem from './ModuleListItem'

export default class BetaModuleList extends React.Component {
    constructor(props) {
        super(props);
        this.selectedModule = props.selectedModule
        this.selectModule = props.selectModule
        this.modules = props.modules
        this.state = {
            module: {
                id: -1,
                title: 'New Module'
            }
        }
    }

    createModule = () => {
        // this.state.module.push()
        this.state.module.id = (new Date()).getTime()
        this.setState({
            modules: [...this.state.modules, this.state.module]
        })
    }

    titleChanged = (event) => {
        console.log(event.target.value)
        this.setState({
            module: {
                title: event.target.value,
                id: (new Date()).getTime()
            }
        })
    }


    render() {
        return(
            <div>
                <h3>Module List</h3>
                <ul className="list-group">
                    {/* <li className="list-group-item">
                        <input
                            onChange={this.titleChanged}
                            defaultValue={this.state.module.title}
                            className="form-control"/>
                        <button onClick={this.createModule} className="btn btn-primary btn-block">
                            Add Module
                        </button>
                    </li> */}
                    {
                        this.modules.map(
                            (module, key) =>
                                <ModuleListItem
                                    deleteModule="#"
                                    selectModule={this.selectModule}
                                    selectedModule={this.selectedModule}
                                    module={module}
                                    key={key}/>
                        )
                    }
                </ul>
            </div>
        )
    }
}
