import React from 'react'

export default class BetaLessonTabs extends React.Component {
    constructor(props) {
        super(props);
        this.lessons = props.lessons
        this.selectLesson = props.selectLesson
        this.selectedLesson = props.selectedLesson
        this.state = {
            lesson: {
                id: -1,
                title: 'New Lesson'
            }
        }
    }

    createLesson = () => {
        // this.state.module.push()
        this.state.lesson.id = (new Date()).getTime()
        this.setState({
            modules: [...this.lessons, this.state.lesson]
        })
    }

    deleteModule = (id) => {
        console.log('deleteModule ' + id)
        this.setState({
            modules: this.state.modules.filter(module => module.id !== id)
        })
    }

    titleChanged = (event) => {
        console.log(event.target.value)
        this.setState({
            lesson: {
                title: event.target.value,
                id: (new Date()).getTime()
            }
        })
    }

    render() {
        return (
            <ul className="nav nav-tabs">
                {
                this.lessons.map((lesson, key) =>
                    <li className={lesson===this.selectedLesson? "nav-item active":"nav-item"} 
                        onClick={() => this.selectLesson(lesson)} key={key}>
                        <a className="nav-link active">
                            {lesson.title}
                        </a>
                    </li>
                    )
                }
                <li className="nav-item">
                    <div className="input-group-append">
                    <input
                        onChange={this.titleChanged}
                        defaultValue={this.state.lesson.title}
                        className="form-control col-4"/>

                        <button onClick={this.createLesson} className="btn btn-primary">
                            Add Lesson
                        </button>
                    </div>
                </li>
            </ul>
        )
    }
}
